mapper.select:
SELECT question_no
FROM question
WHERE test_no = #{testNo} AND question_idx = #{questionIdx}

SELECT COUNT(question_idx)
FROM question
WHERE test_no = #{testNo}

mapper.insert:
INSERT INTO question (
	test_no
	, question_idx
	, question_title
) VALUES (
	#{testNo}
	, #{questionIdx}
	, #{questionTitle}
)
