mapper.insert:
INSERT INTO test (
	test_title
	, test_date
	, test_state
) VALUES (
	#{testTitle}
	, #{testDate}
	, '작성중'
)

mapper.select:
SELECT test_no testNo , test_title testTitle , test_date testDate , test_state testState FROM test
test_date like CONCAT('%', #{testDate}, '%')
ORDER BY test_no DESC;
